<?php

declare(strict_types=1);


if (!function_exists('__valueToString')) {
    function __valueToString(mixed $value): string
    {
        if (\is_string($value)) {
            return '"' . $value . '"';
        }

        if (is_null($value)) {
            return 'null';
        }

        if (true === $value) {
            return 'true';
        }

        if (false === $value) {
            return 'false';
        }

        if (\is_array($value)) {
            return var_export($value, true) ?? 'array';
        }

        if (\is_object($value)) {
            if (\method_exists($value, '__toString')) {
                return \get_class($value) . ': ' . __valueToString($value->__toString());
            }

            if ($value instanceof \DateTime || $value instanceof \DateTimeImmutable) {
                return \get_class($value) . ': ' . __valueToString($value->format('c'));
            }

            return \get_class($value);
        }

        if (\is_resource($value)) {
            return 'resource';
        }

        return (string) $value;
    }
}

if (!function_exists('__dump')) {
    function __dump(): void
    {
        $str = '';
        $isCli = defined('STDIN');

        ob_start();
        foreach (func_get_args() as $x) {
            var_dump($x);
            echo $isCli ? "\n\n" : '<hr class="divider-dd">';
        }
        $str = ob_get_clean();

        $backtrace = debug_backtrace(DEBUG_BACKTRACE_PROVIDE_OBJECT, 2);
        $line = $backtrace[1]['line'];
        $file = $backtrace[1]['file'];

        if ($isCli) {
            echo PHP_EOL . "üîç\033[0;33m ‚Æï \033[0;36m{$file}\033[90m:{$line}\033[0m" . PHP_EOL . $str . PHP_EOL;
            return;
        }

        echo "<pre class='language-dd line-dd'>üîç ‚Æï $file:$line</pre>";
        echo "<pre class='language-dd'><code class='language-dd'>$str</code></pre>";

        // don't forget to escap "\r\n"
        echo <<<JS_PRISM
