if (!function_exists('__dump_trace')) {
    function __dump_trace(bool $isFull): void
    {
        $backtrace = array_slice(debug_backtrace(DEBUG_BACKTRACE_PROVIDE_OBJECT), 2);

        if ($isFull) {

            ob_start();
            var_dump($backtrace);
            echo PHP_EOL . PHP_EOL;
            $str = ob_get_clean();
            echo PHP_EOL;

            if (defined('STDIN')) {
                echo $str;
                return;
            }

            echo '<pre>' . $str . '</pre>';
        }

        $formattedTrace = [''];

        foreach ($backtrace as $v) {
            if (!isset($v['file'])) {
                continue;
            }

            $msg = '‚û´' . $v['file'] . ':' . $v['line'] . "\n";
            if (!empty($v['class'])) {
                $msg .= $v['class'];
                $msg .= $v['type'] ?? '';
            }

            if (!empty($v['function'])) {
                $msg .= $v['function'];
            }

            if (isset($v['args'])) {
                if (count($v['args']) === 0) {
                    $msg .= '()';
                } else {
                    $arr = array_map('__valueToString', $v['args']);
                    $msg .= '(' . implode(', ', $arr) . ')';
                }
            }

            $formattedTrace[] = $msg;
        }

        if (defined('STDIN')) {
            echo implode("\n\n", $formattedTrace);
            return;
        }

        echo '<pre>' . implode("\n\n", $formattedTrace) . '</pre>';

    }
}

if (!function_exists('d')) {
    function d(): void
    {
        $args = func_get_args();
        __dump(...$args);
    }
}

if (!function_exists('dd')) {
    function dd(): void
    {
        $args = func_get_args();
        __dump(...$args);
        die(0);
    }
}

if (!function_exists('dds')) {
    function dds(): void
    {
        $args = func_get_args();
        __dump(...$args);
        __dump_trace(false);
        die(0);
    }
}

if (!function_exists('ddt')) {
    function ddt(): void
    {
        $args = func_get_args();
        __dump(...$args);
        __dump_trace(true);
        die(0);
    }
}
